#[cfg(test)]
mod tests {
    use num::bigint::BigInt;
    #[allow(non_snake_case)]
    #[test]
    fn capture_the_flag() {
        let a = BigInt::parse_bytes(b"", 10).unwrap();
        let b = BigInt::parse_bytes(b"", 10).unwrap();
        let p = BigInt::parse_bytes(b"", 16).unwrap();
        let g = BigInt::from(2);
        let A = g.modpow(&a, &p);
        let B = g.modpow(&b, &p);

        assert_eq!(A.modpow(&b, &p), B.modpow(&a, &p));
    }
}
